<?php
/**
 * WooCommerce Navigation
 * NOTE: DO NOT edit this file in WooCommerce core, this is generated from woocommerce-admin.
 *
 * @package Woocommerce Admin
 */

namespace Automattic\WooCommerce\Admin\Features;

/**
 * Contains logic for the WooCommerce Navigation.
 */
class Navigation {
	/**
	 * Class instance.
	 *
	 * @var Navigation instance
	 */
	protected static $instance = null;

	/**
	 * Array index of menu title.
	 *
	 * @var int
	 */
	const TITLE = 0;

	/**
	 * Array index of menu capability.
	 *
	 * @var int
	 */
	const CAPABILITY = 1;

	/**
	 * Array index of menu callback.
	 *
	 * @var int
	 */
	const CALLBACK = 2;

	/**
	 * Array index of menu CSS class string.
	 *
	 * @var int
	 */
	const CSS_CLASSES = 4;

	/**
	 * Array index of menu handle.
	 *
	 * @var int
	 */
	const HANDLE = 5;

	/**
	 * Store related capabilities.
	 *
	 * @var array
	 */
	protected $store_capabilities = array(
		'manage_woocommerce',
		'view_woocommerce_reports',
	);

	/**
	 * Menu items that are permanently visible.
	 *
	 * @var array
	 */
	protected $permanent_items = array(
		'menu-dashboard',
		'toplevel_page_woocommerce',
	);

	/**
	 * Store related post types.
	 *
	 * @var array
	 */
	protected $post_types = array(
		'bookable_resource',
		'event_ticket',
		'product',
		'shop_coupon',
		'shop_order',
		'shop_subscription',
		'wc_booking',
		'wc_membership_plan',
		'wc_pickup_location',
		'wc_product_tab',
		'wc_user_membership',
		'wc_voucher',
		'wc_zapier_feed',
		'wishlist',
	);

	/**
	 * Store top level categories.
	 *
	 * @var array
	 */
	protected static $categories = array();

	/**
	 * Store related menu items.
	 *
	 * @var array
	 */
	protected static $menu_items = array();

	/**
	 * Screen IDs of registered pages.
	 *
	 * @var array
	 */
	protected static $screen_ids = array();

	/**
	 * Check if we're on a WooCommerce page
	 *
	 * @return bool
	 */
	public function is_woocommerce_page() {
		global $pagenow, $plugin_page;

		// Get post type if on a post screen.
		$post_type = '';
		if ( in_array( $pagenow, array( 'edit.php', 'post.php', 'post-new.php' ), true ) ) {
			if ( isset( $_GET['post'] ) ) { // phpcs:ignore CSRF ok.
				$post_type = get_post_type( (int) $_GET['post'] ); // phpcs:ignore CSRF ok.
			} elseif ( isset( $_GET['post_type'] ) ) { // phpcs:ignore CSRF ok.
				$post_type = sanitize_text_field( wp_unslash( $_GET['post_type'] ) ); // phpcs:ignore CSRF ok.
			}
		}
		$post_types = apply_filters( 'woocommerce_navigation_post_types', $this->post_types );

		// Get current screen ID.
		$current_screen = get_current_screen();
		$screen_ids     = apply_filters( 'woocommerce_navigation_screen_ids', self::$screen_ids );

		if (
			in_array( $post_type, $post_types, true ) ||
			in_array( $current_screen->id, self::$screen_ids, true )
		) {
			return true;
		}

		return false;
	}

	/**
	 * Get class instance.
	 */
	final public static function instance() {
		if ( ! static::$instance ) {
			static::$instance = new static();
		}
		return static::$instance;
	}

	/**
	 * Constructor
	 */
	public function __construct() {
		if ( is_admin() && ! apply_filters( 'woocommerce_use_legacy_navigation', false ) ) {
			add_filter( 'add_menu_classes', array( $this, 'add_core_menu_items' ) );
			add_filter( 'add_menu_classes', array( $this, 'add_menu_settings' ), 20 );
		}
	}

	/**
	 * Add the core menu items to the new navigation
	 */
	public function add_core_menu_items() {
		// Orders category.
		self::add_menu_category(
			__( 'Orders', 'woocommerce-admin' ),
			'edit_shop_orders',
			'orders',
			'edit.php?post_type=shop_order'
		);

		// Products category.
		self::add_menu_category(
			__( 'Products', 'woocommerce-admin' ),
			'edit_products',
			'products',
			'edit.php?post_type=product'
		);

		// Extensions category.
		self::add_menu_category(
			__( 'Extensions', 'woocommerce-admin' ),
			'activate_plugins',
			'extensions',
			'plugins.php'
		);
		self::add_menu_item(
			'extensions',
			__( 'My extensions', 'woocommerce-admin' ),
			'manage_woocommerce',
			'my-extensions',
			'plugins.php'
		);
		self::add_menu_item(
			'extensions',
			__( 'Marketplace', 'woocommerce-admin' ),
			'manage_woocommerce',
			'marketplace',
			'wc-addons'
		);

		// Settings category.
		self::add_menu_category(
			__( 'Settings', 'woocommerce-admin' ),
			'manage_woocommerce',
			'settings',
			'wc-settings'
		);

		// Tools category.
		self::add_menu_category(
			__( 'Tools', 'woocommerce-admin' ),
			'manage_woocommerce',
			'tools',
			'wc-status'
		);
		self::add_menu_item(
			'tools',
			__( 'System status', 'woocommerce-admin' ),
			'manage_woocommerce',
			'system-status',
			'wc-status'
		);
		self::add_menu_item(
			'tools',
			__( 'Import / Export', 'woocommerce-admin' ),
			'import',
			'import-export',
			'import.php'
		);
		self::add_menu_item(
			'tools',
			__( 'Utilities', 'woocommerce-admin' ),
			'manage_woocommerce',
			'utilities',
			'admin.php?page=wc-status&tab=tools'
		);

		// User profile.
		self::add_menu_category(
			wp_get_current_user()->user_login,
			'read',
			'profile',
			'profile.php'
		);
	}

	/**
	 * Convert a WordPress menu callback to a URL.
	 *
	 * @param string $callback Menu callback.
	 * @return string
	 */
	public static function get_callback_url( $callback ) {
		$pos  = strpos( $callback, '?' );
		$file = $pos > 0 ? substr( $callback, 0, $pos ) : $callback;
		if ( file_exists( ABSPATH . "/wp-admin/$file" ) ) {
			return $callback;
		}
		return 'admin.php?page=' . $callback;
	}

	/**
	 * Adds a top level menu item to the navigation.
	 *
	 * @param string $title Menu title.
	 * @param string $capability WordPress capability.
	 * @param string $slug Menu slug.
	 * @param string $url URL or menu callback.
	 * @param string $icon Menu icon.
	 * @param int    $order Menu order.
	 */
	public static function add_menu_category( $title, $capability, $slug, $url = null, $icon = null, $order = null ) {
		self::$categories[] = array(
			'title' => $title,
			'slug'  => $slug,
			'url'   => self::get_callback_url( $url ),
			'icon'  => $icon,
			'order' => $order,
		);

		// @todo Remove from the main menu if the callback matches an existing item.

		self::$screen_ids[] = get_plugin_page_hookname( $url, null );
	}

	/**
	 * Adds a child menu item to the navigation.
	 *
	 * @param string $parent_slug Parent item slug.
	 * @param string $title Menu title.
	 * @param string $capability WordPress capability.
	 * @param string $slug Menu slug.
	 * @param string $url URL or menu callback.
	 * @param string $icon Menu icon.
	 * @param int    $order Menu order.
	 */
	public static function add_menu_item( $parent_slug, $title, $capability, $slug, $url = null, $icon = null, $order = null ) {
		self::$menu_items[ $parent_slug ][] = array(
			'title' => $title,
			'slug'  => $slug,
			'url'   => self::get_callback_url( $url ),
			'icon'  => $icon,
			'order' => $order,
		);

		// @todo Remove from the main menu if the callback matches an existing item.

		// @todo Get parent key by looping over parents first.
		// self::$screen_ids[] = get_plugin_page_hookname( $url, $parent_key );
	}

	/**
	 * Add the menu to the page output.
	 *
	 * @param array $menu Top level dashboard menu items.
	 * @return array
	 */
	public function add_menu_settings( $menu ) {
		global $submenu, $parent_file, $typenow, $self;

		$categories = self::$categories;
		foreach ( $categories as $index => $category ) {
			$categories[ $index ]['children'] = isset( self::$menu_items[ $category['slug'] ] )
				? self::$menu_items[ $category['slug'] ]
				: array();
		}

		$data_registry = \Automattic\WooCommerce\Blocks\Package::container()->get(
			\Automattic\WooCommerce\Blocks\Assets\AssetDataRegistry::class
		);

		$data_registry->add( 'wcNavigation', $categories );

		return $menu;
	}
}
